    public function getSourceContext()
    {
        return new Twig_Source("{% extends 'base-admin.html.twig' %}

{% block title %}Liste des categories{% endblock %}

{% block stylesheets %}
<style>
table {
  border-collapse: collapse;
  width: 100%;
}

th, td {
  text-align: left;
  padding: 8px;
}

tr:nth-child(even){background-color: #f2f2f2}

th {
  background-color: #0aa;
  color: white;
}
</style>



{% endblock %}

{% block body %}
    <h1><i class=\"green\">Liste des categories</i></h1>

    <table class=\"table\" >
        <thead>
            <tr>
                <th>Id</th>
                <th>Libelle</th>
                <th>Description</th>
                <th>Image</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for categorie in categories %}
            <tr>
                <td>{{ categorie.id }}</td>
                <td>{{ categorie.libelle }}</td>
                <td>{{ categorie.description | raw }}</td>
                <td><img height=\"100px\" width=\"100px\" src=\"{{ categorie.image }}\" /></td>
                <td>
                    <a href=\"{{ path('categorie_show', {'id': categorie.id}) }}\">Afficher</a>
                    <a href=\"{{ path('categorie_edit', {'id': categorie.id}) }}\">Modifier</a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"5\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <a class=\"btn btn-success\" href=\"{{ path('categorie_new') }}\">Ajouter une categories</a>
{% endblock %}
